    public SysexBuilder getVoice(int memory_type, int memory_number){
        //tg77.get(
        ArrayList<Integer> data = getData(getRequest("LM  8101VC", memory_type, memory_number).getSysex(), 1);
        System.out.println(data.size());
        System.out.println(debugSysex(data));
//List<Integer> head_data = data.subList(4, 43);
        SysexBuilder head_builder = toBuilder(data.subList(1, 44), LM_8101VC_CHECK);
        head_builder.getExplanations().print();
        System.out.println(head_builder.getValueText("vc_header.elmode"));
        int elmode = head_builder.getInt("vc_header.elmode");
        switch(elmode){
            case 0:
            case 3:
                return  toBuilder(data.subList(1, data.size() - 1), LM_8101VC_0_3);
            case 1:
            case 4:
                return  toBuilder(data.subList(1, data.size() - 1), LM_8101VC_1_4);
            case 2:
                return  toBuilder(data.subList(1, data.size() - 1), LM_8101VC_2);
            case 5:
                return  toBuilder(data.subList(1, data.size() - 1), LM_8101VC_5);
            case 6:
                return  toBuilder(data.subList(1, data.size() - 1), LM_8101VC_6);
            case 7:
                return  toBuilder(data.subList(1, data.size() - 1), LM_8101VC_7);
            case 8:
                return  toBuilder(data.subList(1, data.size() - 1), LM_8101VC_8);
            case 9:
                return  toBuilder(data.subList(1, data.size() - 1), LM_8101VC_9);
            default: throw new OmException("cannot find elmode for 0x" + OmUtil.hex(elmode));
        }
    }
    
    public SysexBuilder getRequest(String str){
        return getRequest(str, 0, 0);
    }
    public SysexBuilder getRequest(String str, int memory_type, int memory_number){
        int[] data_req = new int[26];
        for(int i = 0;i < 24;i++){
            data_req[i] = i < 10?str.charAt(i):0x00;
        }
        data_req[24] = memory_type;
        data_req[25] = memory_number;
        return getRequest(data_req, REQUEST);
    }
